
public class Ejercicio_2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		final double dolar=1.137;
		final double salario=2125.70;
		
		String nombre="Manuel";    //variable String se utiliza para una cadena de caracteres
		
		double salario_en_dolar=salario*dolar;
		
		System.out.println("El salario en dolares de "+ nombre + " es " + salario_en_dolar);
		
		final int dias_semana=7;
		final int dias_laborables=5;
		String trabajador="alex";
		
		
		System.out.println("Los dias de la semana laborables son "+ dias_semana + "y los dias laborables son "+ 
		dias_laborables + "y  "+ trabajador +" trabaja"+ dias_laborables + "dias");
	}

}
