
public class clase_String3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String alumno1,alumno2;
		alumno1="Francisco";
		alumno2="francisco";
		System.out.println(alumno1.equals(alumno2)); //equals valora si dos cadenas de caracteres son iguales
		
		String nombre1,nombre2;
		nombre1="Paco";
		nombre2="paco";
		System.out.println(nombre1.equalsIgnoreCase(nombre2));
		

	}

}
